import React, { Component } from 'react';
import Checkedd from './components/checkbox';

class Homework extends Component {
    constructor(props) {
        //create state
        super(props);
        this.handlehomework= this.handlehomework.bind(this)
        this.state = {
            
        };
    }
    handlehomework(homework, myswitch){
        let myhomework = {
            title: "my homework",
            description: "my des",
            hwchecked: {
                mon: false,
                tues: false,
                wed: false,
                thur: false,
                fri: false,
                sat: false,
                sun: false,
            },
            hwtype: "assignment",
            date: "",
            research: "",
            daily: "5",
            hwsynccheck: true,
            hwlink: "link",
            hwdmin: "20",
            hwtimesync: true,
            hwtime: {
                mon: "0",
                tues: "0",
                wed: "0",
                thur: "0",
                fri: "0",
                sat: "0",
                sun: "0",
            },
        }
        

    }
    //{this.props.dispatch.bind(this, {goals:true, main: true})}

    render() {
       

        return (
            <div className="bigcard" >
            <h3>Homework</h3>
            <div style={{display:"flex", flexDirection:"column"}}>
            <button  className="btn  btn-block"  onClick={this.handlehomework.bind(this, "", "addhomework")} style={{ background: "#696eb5", height: "30px", color: "#F0F2EF", width: "200px", display:"flex", flexDirection:"column", justifyContent:"center", alignItems:"center"}}><span style={{display:"flex", flexDirection:"row"}}>+ Homework</span></button>
               <Checkedd size={this.props.state.styles.checkbox.size1} state={this.props.state} student={this.props.state.currentstudent} handleChange={this.props.handleChange} dispatch={this.props.dispatch}/>
               {this.props.state.currentstudent?.homeworks.map((homework, index) => <div key={index}>{homework} </div>)}
            </div>
            </div>

               
        );
    }
}

export default Homework;
