            let arr = [];
            for (let i = 0; i < this.state.notes.length; i++) {
                

                if (this.state.notes[i]._id !== id) {
                    arr.push(this.state.notes[i]);

                }

               
            }
            console.log(arr);

            this.setState({
                notes: arr,
            })

            AuthService.deletenote(this.props.student, id);

        }

