import React, { Component } from 'react';
import leaf from "./leaf.png"
import Checkedd2 from './components/checkbox2';
import authService from '../services/auth.service';
import studentService from '../services/studentService';
class Goals extends Component {
    constructor(props) {
        //create state
        super(props);
        this.AddUpdateDeleteGoal=this.AddUpdateDeleteGoal.bind(this);
        this.state = {
            goal: {title: "new supporting goal",description: "",complete: false,completed: "",id:Date.now()},
            maingoal:{mainGoal:{complete: false,completed: "",date: "",description:"",title:"New goal added",goals:[], id:Date.now()}}
        };
    }
    /**
     * add goals.
     * @param {*} main 
     * @param {*} maingoal 
     */
    async AddUpdateDeleteGoal(goal, maingoal, myswitch){
        debugger
        let mygoal = !goal? false: {...goal};
        maingoal=maingoal?{...maingoal}: {...this.state.maingoal};
        let currentmains = await studentService.updateAddGoals(mygoal, this.props.state.currentstudent.mainGoals, maingoal, myswitch)
        this.props.dispatch({[this.props.state.currentstudent.mainGoals]:currentmains})
        authService.changeData("student", this.props.state.currentstudent._id, this.props.state.currentuser._id, {mainGoals: currentmains})


    }


    render() {


        return (
            <div className="smallcardleft" style={{ marginTop: this.props.state.styles.margins.margin1 }}>
                <div style={{display:"flex", flexDirection:"row", justifyContent:"space-between"}}><h3>Goals</h3><button  className="btn  btn-block"  onClick={this.AddUpdateDeleteGoal.bind(this, false, false, "addmain")} style={{ background: "#696eb5", height: "30px", color: "#F0F2EF", width: "100px", display:"flex", flexDirection:"column", justifyContent:"center", alignItems:"center"}}>+ Goal</button></div>
                <div style={{ display: "flex", flexDirection: "column" }}>
                    {this.props.state.currentstudent.mainGoals.map((main, index) =>
                        <div key={index} >
                            <div>
                                <div style={{ display: 'flex', flexDirection: "row", justifyContent: "space-between", }}>
                                    <div style={{ display: 'flex', flexDirection: "row", alignItems: "center" }}>
                                        <Checkedd2 myswitch="updatemain" size= {this.props.state.styles.checkbox.size1} maingoal={main} state={this.props.state} main={true} AddUpdateDeleteGoal={this.AddUpdateDeleteGoal}/>
                                        <h4 style={{ marginBottom: "10px" }} className="huv rowss" ><span>{main.mainGoal.title} </span></h4>
                                    </div>
                                    <div>
                                        <div >edit</div>
                                        <div onClick={this.AddUpdateDeleteGoal.bind(this, false, main, "delmain")} >delete</div>
                                    </div>
                                </div>
                            </div>
                            {main.mainGoal.goals.map((goal, index) =>
                                <div key={index} style={{ marginLeft: "30px" }} >
                                    <div style={{ display: "flex", flexDirection: "row" }}>
                                            <Checkedd2 myswitch="updategoal" size= {this.props.state.styles.checkbox.size1} goal={goal} maingoal={main} state={this.props.state} main={false} AddUpdateDeleteGoal={this.AddUpdateDeleteGoal}/>
                                        {goal.title}
                                    </div>
                                    <div style={{ width: "25%", flexDirection: "row", justifyContent: "flex-end", display: "flex", }}>
                                        <div >edit</div>
                                        <div onClick={this.AddUpdateDeleteGoal.bind(this, goal, main, "delgoal")}>delete</div>
                                    </div>
                                </div>
                            )}
                            <div className="btn  btn-block" style={{ margin: "0 auto", marginTop: "10px", display: 'flex', flexDirection: 'row', justifyContent: 'center' }} value="submit" onClick={this.AddUpdateDeleteGoal.bind(this, this.state.goal, main, "addgoal" )}>
                                <span className="checkboxstuff1" style={{ width: "250" }}><img src={leaf} style={{ width: "20px", height: "20px" }} /><p>+</p><p className="rowss huv">Supporting Goal</p></span></div>
                        </div>)}
                </div>
            </div>
        );
    }
}

export default Goals;
