                        if (apptTime[1] === "1") {
                            ar = ar + "9";
                        }
                        if (apptTime[1] === "2") {
                            ar = ar + "10";
                        }
                        if (apptTime[1] === "3") {
                            ar = ar + "11";
                        }
                    }
                    else if (apptTime[0] === "0") {
                        if (apptTime[1] === "0") {
                            ar = ar + "12";
                            ampm = true;
                        }
                    }
                }
                else {
                    console.log(apptTime);
                    ar = ar + apptTime[i];

                }

            }



            else {
                if (apptTime.length === 3) {
                    console.log(i);
                    if (i === 1) {
                        ar = ar + ":";

                    }
                    console.log(apptTime);
                    ar = ar + apptTime[i];

                    if (i === 2) {
                        ar = ar + " AM";
                    }


                }
                else {
                    if (apptTime[j]) {
                        if (i === 1) {
                            ar = ar + ":";
                        }

                        ar = ar + apptTime[j];
                        if (i === 2) {
                            if (ampm) {
                                ar = ar + " AM";
                            }
                            else { ar = ar + " PM"; }

                        }

                    }
                }


            }

            console.log(ar);

        }
        return ar;
