import React, { Component } from 'react';
import leaf from "./leaf.png"
import Checkedd2 from './components/checkbox2';
class Goals extends Component {
    constructor(props) {
        //create state
        super(props);
        this.AddGoal=this.AddGoal.bind(this)
        this.state = {

        };
    }
    AddGoal(main){
        if(main){
            let currentmains= this.props.state.currentstudent.mainGoals
            let maingoal={
                complete: false,
                completed: "",
                date: "",
                description:"",
                title:"New goal added",
                goals:[]
            }
            currentmains.push(maingoal)
            this.props.dispatch({[this.props.state.currentstudent.mainGoals]:currentmains})
        }

    }


    render() {


        return (
            <div className="smallcardleft" style={{ marginTop: this.props.state.styles.margins.margin1 }}>
                <div style={{display:"flex", flexDirection:"row", justifyContent:"space-between"}}><h3>Goals</h3><button  className="btn  btn-block"  onClick={this.AddGoal.bind(this, true)} style={{ background: "#696eb5", height: "30px", color: "#F0F2EF", width: "100px", display:"flex", flexDirection:"column", justifyContent:"center", alignItems:"center"}}>+ Goal</button></div>
                <div style={{ display: "flex", flexDirection: "column" }}>
                    {this.props.state.currentstudent.mainGoals.map((main, index) =>
                        <div key={index} >
                            <div>
                                <div style={{ display: 'flex', flexDirection: "row", justifyContent: "space-between", }}>
                                    <div style={{ display: 'flex', flexDirection: "row", alignItems: "center" }}>
                                        <Checkedd2 size= {this.props.state.styles.checkbox.size1}/>
                                        {/*  goal={main} goalStatusChange={this.completeGoal} main={true}*/}
                                        <h4 style={{ marginBottom: "10px" }} className="huv rowss" ><span>{main.mainGoal.title} </span></h4>
                                    </div>
                                    <div>
                                        <div >edit</div>
                                        <div >delete</div>
                                    </div>
                                </div>
                            </div>
                            {main.mainGoal.goals.map((goal, index) =>
                                <div key={index} style={{ marginLeft: "30px" }} >
                                    <div style={{ display: "flex", flexDirection: "row" }}>
                                            <Checkedd2 size= {this.props.state.styles.checkbox.size1}/>
                                            {/*  goal={goal} goalStatusChange={this.completeGoal} main={false}*/}
                                        {goal.title}
                                    </div>
                                    <div style={{ width: "25%", flexDirection: "row", justifyContent: "flex-end", display: "flex", }}>
                                        <div >edit</div>
                                        <div >delete</div>
                                    </div>
                                </div>
                            )}
                            <div className="btn  btn-block" style={{ margin: "0 auto", marginTop: "10px", display: 'flex', flexDirection: 'row', justifyContent: 'center' }} value="submit" onClick={this.AddGoal.bind(this, main.mainGoal)}>
                                <span className="checkboxstuff1" style={{ width: "250" }}><img src={leaf} style={{ width: "20px", height: "20px" }} /><p>+</p><p className="rowss huv">Supporting Goal</p></span></div>
                        </div>)}
                </div>
            </div>
        );
    }
}

export default Goals;
