import React, { Component } from "react";
//details my existingEmail.js component. creates some buttons that use methods embedded in props from the profile page. Choice will update the backend.
class Addhomework extends Component {
    constructor(props) {
        super(props);
        this.wrapperRef = React.createRef();
        this.handleClickOutside = this.handleClickOutside.bind(this);
        this.setWrapperRef = this.setWrapperRef;
        this.state = {
        }

    };
    componentDidMount() {
        if(parseInt(window.innerWidth) <= 550){
            this.setState({ tooSmall: true });
            }
        document.addEventListener('mousedown', this.handleClickOutside);
    }
    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClickOutside);
    }
    handleClickOutside(event) {
        if (this.wrapperRef && !this.wrapperRef.current.contains(event.target)) {
            this.props.handleClosing();
        }
    }
    render() {
        return (
            <div className="popup-box" style={{ zIndex: "1010" }}>
                <div ref={this.wrapperRef}  className="diapicboxa" style={{ zIndex: "1010", height:"50vh" }}>

                    
                    <span className="close-icon-2" onClick={this.props.handleClosing}>x</span>
                    <div >
                    <div className="form-group">
                        <label htmlFor="lastName"><b>Homework Name:</b></label>
                        <input type="text" className="form-control" id="homework" style={{width:"95%"}} onChange={this.props.handleChange} name="title"/>
                    </div>
                        <div className="form-group">
                            <label>Describe the practice assignment.</label>
                                <textarea type="text" className="form-control" rows="3" id="hwdescription" onChange={this.props.handleChange} name="description"></textarea>
                        </div>
                        <div className="form-group">
                            <label>Do you want to use checkboxes to track this practice assignment's progress? </label>
                                    <select htmlfor="yesnoCheckbox" onChange={this.props.handleChange} name="checkbox" id="yesnoCheckbox"><option value={true}>yes</option> <option value={false}>no</option></select>
                        </div>
                        <div className="form-group">
                            <label>Do you want to use time to track this practice assignment's progress? </label>
                                    <select htmlfor="yesnoCheckbox" onChange={this.props.handleChange} name="time" id="yesnoCheckbox"><option value={true}>yes</option> <option value={false}>no</option></select>
                        </div>                    
                        <div style={{ marginTop: "20px", }}>
                        <button  className="btn  btn-block"  onClick={this.props.handlehomework.bind(this, "", "addhomework")} style={{ background: "#696eb5", height: "30px", color: "#F0F2EF", width: "200px", display:"flex", flexDirection:"column", justifyContent:"center", alignItems:"center"}}>+ Homework</button>
                    </div>
                    </div>
                </div>
            </div>

        )
    }
};
export default Addhomework;
