import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
//uploade above stuff from npm.
import "./App.css";
import AuthService from "./services/auth.service";
import Dashboard from "./view/dashboard.js"
import StudentProfile from "./view/dashboard.js"
import Login from './view/signin.js';
import Addstudent from './view/popups/addStudent';
import authService from './services/auth.service';
// import Register from "./pages/usersignup";
// import Login from "./pages/signin";
// import Home from "./pages/home";
// import About from "./pages/about";
// import Metro from "./pages/metro";
// import Contact from "./pages/contact";
// import UsrProfile from "./pages/userProfile";
// import Profile from "./pages/profile";
// import Calendar from "./pages/calendar";
// import StudentsTeacher from "./pages/students.teacher";
// import legato from "./legato.png";
// import Admin from "./services/admin.js";
// import Menu from "./menu.png";

// //import BoardUser from "./components/board-user.component";
// import Student from "./pages/student";
// import student_routes from "./components/student_routes";

//nav bar helps to navigate from page to page with authorizations to login or sign up etc. 
class App extends Component {
    constructor(props) {
        super(props);

        this.handleChange=this.handleChange.bind(this)
        this.statesetter=this.statesetter.bind(this)
        this.state = {
            viewstudent: false,
            metro: false,
            calendar: false,
            studentlist: false,
            student: false,
            currentuser: false,
            role: "",
            email: "",
            password: "",
            addstudent:false
        };
    }
    componentDidMount(){
        if(this.state.currentuser){
        authService.signin(this.state.currentuser.email, false, true)
        }
    }
 
    statesetter(obj){
        for (const key in obj){
            this.setState({[key]:obj[key]})
        }

    }
    handleChange = (event) => {
        const { name, value } = event.target
        this.setState({
            [name]: value,
        })
    }
    render() {
        return (
            <div>
                {this.state.addstudent && (<Addstudent state={this.state} statesetter={this.statesetter} handleClose={this.statesetter.bind(this, {addstudent:false})}/>)}
                {this.state.currentuser?(<div>
                    {this.state.role==="student"?(<StudentProfile handleChange={this.handleChange} statesetter={this.statesetter} state={this.state}/>
                        ):(
                        <Dashboard handleChange={this.handleChange} statesetter={this.statesetter} state={this.state}/>)}
                </div>):(<Login handleChange={this.handleChange} handleLogin={this.statesetter} state={this.state} />)}
                
            
            </div>
        );
    }
}
export default App;
