  handleProgressChange(event) {
    const {
      name,
      value
    } = event.target;

    if (value !== "no change") {
      if (name === "tempstartpoints") {
        this.setState({
          spisedited: true
        });
      }

      if (name === "temppracticegoal") {
        this.setState({
          goalisedited: true
        });
      }

      if (name === "tempdays") {
        if (value !== "") {
          this.setState({
            dayisedited: true
          });
        } else {
          this.setState({
            dayisedited: false
          });
        }
      }

      if (name === "tempsmonths") {
        this.setState({
          smonthsisedited: true
        });
      }

      if (name === "tempemonths") {
        this.setState({
          emonthsisedited: true
        });
      }

      if (name === "temptimegoal") {
        this.setState({
          timegoalisedited: true
        });
      }

      if (name === "tempmin") {
        if (value !== "") {
          this.setState({
            minisedited: true
          });
        } else {
          this.setState({
            minisedited: false
          });
        }
      }

      if (name === "temptsmonths") {
        this.setState({
          tsmonthsisedited: true
        });
      }

      if (name === "temptemonths") {
        this.setState({
          temonthsisedited: true
        });
      }

      this.handleChange(event);
    }
  }

